macro.metrics.get_metric.macro_sql:
{% macro get_metric(metric_name) %}
    {% if not execute %}
        {% do return(None) %}
    {% else %}
    {% set metric_info = namespace(metric_id=none) %}
    {% for metric in graph.metrics.values() %}
        {% if metric.name == metric_name %}
            {% set metric_info.metric_id = metric.unique_id %}
        {% endif %}
    {% endfor %}

    {% if metric_info.metric_id is none %}
        {% do exceptions.raise_compiler_error("Metric named '" ~ metric_name ~ "' not found") %}
    {% endif %}


    {% do return(graph.metrics[metric_info.metric_id]) %}
    {% endif %}

{% endmacro %}

macro.metrics.is_dim_from_model.macro_sql:
{% macro is_dim_from_model(metric, dim_name) %}
    {% if execute %}
        -- For now, time dimensions have to be encoded in the meta tag. 
        -- If there's no meta config, then assume all dimensions belong to the main model.
        {% if not metric['meta']['dimensions'] %}
            {% do return(True) %}
        {% endif %}

        {% set model_dims = (metric['meta']['dimensions'] | selectattr('type', '==', 'model') | first)['columns'] %}
        {% do return (dim_name in model_dims) %}
    {% else %}
        {% do return (False) %}
    {% endif %}
{% endmacro %}

